data.value:
SET lc_time_names = 'es_MX'; select v.* from (select s.nombre as instalacion, s.nombre as nombresucursal, s.clavecct as clavesucursal, c.idcurso, c.nombre as nombrecurso, cast(c.fechaini as char)as fechaini, cast(c.fechafin as char) as fechafin, cast(c.horaini as char) as horaini, cast(c.horafin as char) as horafin, c.horas, i.idinstructor, i.nombre as instructor, c.pagohora,TIMESTAMPDIFF(HOUR, c.horaini, c.horafin) AS horaspordia, ( select GROUP_CONCAT(distinct(UPPER(DAYNAME(f.fecha))) SEPARATOR ', ') from fechascurso f where f.idcurso = c.idcurso )as diassemana, ( select count(f.fecha) from fechascurso f where f.idcurso = c.idcurso and f.fecha between @fechaini and @fechafin )as diastrabajo, coalesce(( select sum(TIMESTAMPDIFF(HOUR, f.horaini, f.horafin)) from fechascurso f where f.idcurso = c.idcurso and f.fecha between @fechaini and @fechafin ),0)as horastrabajo
from sucursal s left join curso c on c.idsucursal = s.idsucursal left join instructor i on i.idinstructor = c.idinstructor where s.idsucursal=@idsucursal and c.idtipocurso=@idtipocurso and c.estatus in('AUTORIZADO')
and ( select count(f.fecha) from fechascurso f where f.idcurso=c.idcurso and f.fecha between @fechaini and @fechafin )>0
)as v
order by v.instructor, v.nombrecurso
